mapper.select:
SELECT p.payment_id AS paymentId,
p.member_id AS memberId,
p.account_id AS accountId,
p.card_id AS cardId,
p.resv_id AS resvId,
p.payment_money AS paymentMoney,
p.payment_method AS paymentMethod,
p.payment_status AS paymentStatus,
p.payment_date AS paymentDate
FROM GYM.payment_tbl p
AND p.payment_id = #{req.paymentId}
AND p.member_id = #{req.memberId}
AND p.resv_id = #{req.resvId}
AND p.payment_method = #{req.method}
AND p.payment_status = #{req.paymentStatus}
ORDER BY p.payment_id DESC

mapper.update:
UPDATE GYM.payment_tbl
SET payment_status = #{status}	
WHERE payment_id = #{paymentId}
